import xarray as xr
import dask.array as da
import numpy as np

# (a+b)^2 = a^2 + b^2 + 2ab

a = da.from_array([1, 2, 3, 4], chunks = (1))
b = da.from_array([5, 6, 7, 8], chunks = (1))
xr_a = xr.DataArray(a)
xr_b = xr.DataArray(b)

def add(arr1, arr2):
    return arr1 + arr2

def sq(arr):
    return arr ** 2

def mul(arr1, arr2):
    return arr1 * arr2

a_sq = sq(xr_a) 
b_sq = sq(xr_b)
add_ab = add(a_sq, b_sq) # a^2 +b^2
mul_ab = mul(xr_a, xr_b) # ab

res = add(add_ab, mul_ab)

