# Quote: Ensure that this value remains this value in a dask graph github.com/dask/dask/blob/main/dask/core.py#L515

dask delayed and fuse
import xarray as xr
import dask.array as da
import numpy as np

# (a+b)^2 = a^2 + b^2 + 2ab

a = da.from_array([1, 2, 3, 4], chunks = (1))
b = da.from_array([5, 6, 7, 8], chunks = (1))
xr_a = xr.DataArray(a)
xr_b = xr.DataArray(b)

@dask.delayed
def add_d(arr1, arr2):
    return arr1 + arr2

@dask.delayed
def sq_d(arr):
    return arr ** 2

@dask.delayed
def mul_d(arr1, arr2):
    return arr1 * arr2

a_sq = sq_d(xr_a) 
b_sq = sq_d(xr_b)
add_ab = add_d(a_sq, b_sq) # a^2 +b^2
mul_ab = mul_d(xr_a, xr_b) # ab

res_d = add(add_ab, mul_ab)
delayed_hgl = res_d.data.dask
delayed_hgl.visualize()

